            }
        }
    ],
    detail_row: {
        Activo: { type: String, default: 'S' },
        Borrado: { type: String, default: 'N' },
        detail_row_reg: [
            {
                FechaReg: { type: Date, default: Date.now },
                UsuarioReg: { type: String }
            }
        ]
    }
});

export default mongoose.model('Entrega', entregaSchema, 'entregas');

